package de.heckmeck.livetext;

import org.openqa.selenium.chrome.ChromeDriver;

import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.OutputStream;
import java.net.Socket;

public class Main {
  public static void main(String... args) throws Exception {
    UrlTranscoder transcoder = new UrlTranscoder();
    System.setProperty("webdriver.chrome.driver", "chromedriver.exe");
    ChromeDriver driver = new ChromeDriver();
    driver.get("https://zxnet.co.uk/teletext/editor/#0::PS=0:RE=0:zx=BG0");
    Socket socket = new Socket("192.168.178.100", 2000);
    OutputStream out = socket.getOutputStream();
    for (;;) {
      var url = driver.getCurrentUrl();
      byte[] buf = transcoder.transcodeFromUrl(url);
      for (int i = 0; i < 5; i++) {
        out.write(buf);
        out.flush();
        out.write(transcoder.createFillingHeader(buf));
        out.flush();
      }
      Thread.sleep(500L);
    }
  }

}
